import React from "react";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails, removeSelectedPokemon } from "../redux/actions/index.js";
import './Detail.css'
import NavBar from "./NavBar.jsx";
export default function Detail(props) {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getDetails(props.match.params.id));
  }, [dispatch]);

  let details = useSelector((state) => state.detail);

  return (
    <>
    <NavBar />
    <div className="container">
      
      <div>
        {details.length ? (
          details.map((p) => (   
            <>      
              <div>
                <h1 className="names">{p.name.toUpperCase()}</h1>
                <h2 className="id">#{p.id}</h2>
              </div>
              <div>
                <img  className="imagen" src={p.image} alt="" width="250px" height="250px" />
                {p.types.length === 2 ? (
                  <div>
                    <h3 className="type1">
                    <ul className="type">
                      <li className="typeLiDetail">
                        {
                        typeof p.types[0] === 'string' ? p.types[0] : p.types[0]?.name}-  
                         {
                         typeof p.types[1] === 'string' ? p.types[1] : p.types[1]?.name}
                
                      </li>
                    </ul>
                    </h3>
                  </div>
                ) : (
                  <div>
                    <h3 className="typeLiDetail2">{
                    typeof p.types[0] === 'string' ? p.types[0] : p.types[0]?.name}</h3>
                  </div>
                )}

                <div>
                  <h4>
                  <ul>
                    <li className="lista">
                      Hp: {p.hp} -
                      Attack: {p.attack} -
                      Defense: {p.defense} -
                      Speed: {p.speed} -
                      Height: {p.height} -
                      Weight: {p.weight} 
                    </li>
                  </ul>
                  </h4>
                </div>
              </div> 
              </>              
          ))
        ) : (
          <img
            src={"https://media.tenor.com/fSsxftCb8w0AAAAd/pikachu-running.gif"}
            width="250px" height="300px"
            alt="Not found"
          />
        )}
      </div>
      <div className="backLink">
      <Link to="/home" > Back </Link> </div>
    </div>
    </>
  );
}
