import React from "react";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails } from "../redux/actions/index.js";
import './Detail.css'

export default function Detail(props) {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getDetails(props.match.params.id));
  }, [dispatch]);

  let details = useSelector((state) => state.detail);

  return (
    <div className="container">
      
      <div>
        {details.length ? (
          details.map((p) => (
            <Link to={`/home/${p.id}`}>
              <div>
                <h1 className="names">{p.name.toUpperCase()}</h1>
                <h2 className="id">#{p.id}</h2>
              </div>
              <div>
                <img  className="imagen" src={p.image} alt="" width="250px" height="250px" />
                {p.types.length === 2 ? (
                  <div>
                    <h3 className="type1">
                    <ul className="type">
                      <li>
                        {
                        typeof p.types[0] === 'string' ? p.types[0] : p.types[0]?.name}-  
                         {
                         typeof p.types[1] === 'string' ? p.types[1] : p.types[1]?.name}
                
                      </li>
                    </ul>
                    </h3>
                  </div>
                ) : (
                  <div>
                    <h3 className="type2">{
                    typeof p.types[0] === 'string' ? p.types[0] : p.types[0]?.name}</h3>
                  </div>
                )}

                <div>
                  <h4>
                  <ul>
                    <li className="lista">
                      Hp: {p.hp} -
                      Attack: {p.attack} -
                      Defense: {p.defense} -
                      Speed: {p.speed} -
                      Height: {p.height} -
                      Weight: {p.weight} 
                    </li>
                  </ul>
                  </h4>
                </div>
              </div>
            </Link>
          ))
        ) : (
          <img
            src={"https://media.tenor.com/r6c6mcmv1hEAAAAC/psyduck-psyduckcute.gif"}
            width="250px" height="300px"
            alt="Not found"
          />
        )}
      </div>
      <div className="volver">
      <Link to="/home" className="letter"> Back </Link> </div>
    </div>
  );
}
