        <li className="type">
          
        {types.length === 2 ? (
                  <div>
                    <h3 className="type1">
                    <ul className="type">
                      <li>
                        {
                        typeof types[0] === 'string' ? types[0] : types[0]?.name}-  
                         {
                         typeof p.types[1] === 'string' ? types[1] : types[1]?.name}
                
                      </li>
                    </ul>
                    </h3>
                  </div>
                ) : (
                  <div>
                    <h3 className="type2">{
                    typeof types[0] === 'string' ? types[0] : types[0]?.name}</h3>
                  </div>
                )}
        </li>
      </ul>
    </div>
  );
}
