import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory, useParams } from 'react-router-dom';
import { updatePokemon } from '../redux/actions/index';

const validateForm = ({ name, image, hp, attack, defense, speed, height, weight }) => {
  // validation code here
};

const UpdatePokemonPage = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const { pokemonId } = useParams(); // get the pokemon ID from the URL parameter
  const pokemonToUpdate = useSelector((state) => state.pokemons[pokemonId]); // get the pokemon to update from state

  const [pokemon, setPokemon] = useState({ // set the initial state using the existing pokemon
    name: pokemonToUpdate.name,
    types: pokemonToUpdate.types,
    image: pokemonToUpdate.image,
    hp: pokemonToUpdate.hp,
    attack: pokemonToUpdate.attack,
    defense: pokemonToUpdate.defense,
    speed: pokemonToUpdate.speed,
    height: pokemonToUpdate.height,
    weight: pokemonToUpdate.weight,
  });

  const [errors, setErrors] = useState({}); // store any validation errors

  useEffect(() => {
    setErrors(validateForm(pokemon));
  }, [pokemon]);

  function handleSelect(e) {
    setPokemon({
      ...pokemon,
      types: [...pokemon.types, e.target.value],
    });
  }

  function onInputChange(e) {
    setPokemon({
      ...pokemon,
      [e.target.name]: e.target.value,
    });
  }

  function onSubmit(e) {
    e.preventDefault();
    if (Object.keys(errors).length > 0) {
      alert('Please correct the errors in the form');
      return;
    }
    dispatch(updatePokemon(pokemonId, pokemon)); // send the updated pokemon to the API via the updatePokemon action
    alert('Pokemon has been updated');
    history.push('/home');
  }

  return (
    <>
    <NavBar />
    <div>
      <h1>Update Pokemon</h1>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="name"> Name: </label>
          <input
            onChange={onInputChange}
            id="name"
            name="name"
            type="text"
            value={pokemon.name}
            className="input"
          />
        </div>
        {errors.name && <p className="error"> {errors.name}</p>}
        <div className="form-group">
          <label htmlFor="image">Image: </label>
          <input
            onChange={onInputChange}
            id="image"
            name="image"
            type="text"
            value={pokemon.image}
            className="input"
          />
        </div>
        {errors.image && <p className="error"> {errors.image}</p>}
        <div className="form-group">
          <label htmlFor="hp">Hp: </label>
          <input
            onChange={onInputChange}
            id="hp"
            name="hp"
            type="number"
            value={pokemon.hp}
            className="input"
          />
        </div>
        {errors.hp && <p className="error"> {errors.hp}</p>}
        <div className="form-group">
          <label htmlFor="attack">Attack: </label>
          <input
            onChange={onInputChange}
            id="attack"
            name="attack"
            type="number"
            value={pokemon.attack}
            className="input"
          />
        </div>
        {errors.attack && <p className="error"> {errors.attack}</p>}
     <div className="form-group">
        <label >Defense: </label>
        <input
          onChange={onInputChange}
          name="defense"
          type="number"
          value={pokemon.defense}
          className="input"
        />{" "}
      </div>
     {errors.defense && <p className="error"> {errors.defense}</p>}
     
     <div className="form-group">
        <label >Speed: </label>
        <input
          onChange={onInputChange}
          name="speed"
          type="number"
          value={pokemon.speed}
          className="input"
        />{" "}
      </div>
      {errors.speed && <p className="error"> {errors.speed}</p>}
     
      <div className="form-group">
        {" "}
        <label >Height: </label>
        <input
          onChange={onInputChange}
          name="height"
          type="number"
          value={pokemon.height}
          className="input"
        />{" "}
      </div>
      {errors.height && <p className="error"> {errors.height}</p>}
     
      <div className="form-group">
        <label >Weight: </label>
        <input
          onChange={onInputChange}
          name="weight"
          type="number"
          value={pokemon.weight}
          className="input"
        />{" "}
      </div>
      {errors.weight && <p className="error"> {errors.weight}</p>}
      
        {" "}
      <div className="form-group">
        <label >Types: </label>
        <p className="types">
        <select onChange={handleSelect}>
        <option value="" disabled selected hidden>Type 1</option>
          {types.map((e) => (
            <option  value={e.name}>{e.name}</option>
          ))}{" "}
        </select>
        </p>
        <p className="types">
        <select onChange={handleSelect}>
        <option value="" disabled selected hidden>Type 2</option>
          {types.map((e) => (
            <option  value={e.name}>{e.name}</option>
          ))}{" "}
        </select>
        </p>
      </div>        
      <button type="submit" className="submit">Create Pokemon</button>
    </form>
    </div>
    </>
  );
}
