import React from "react";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetails, removeSelectedPokemon } from "../redux/actions/index.js";
import './Detail.css'
import NavBar from "./NavBar.jsx";
export default function Detail(props) {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getDetails(props.match.params.id));
  }, [dispatch]);

  let details = useSelector((state) => state.detail);

  return (
    <>
    <NavBar />
    <div className="container">
      
      <div>
        {details.length ? (
          details.map((p) => (   
            <> 
              <div>     
                <div>
                  
                </div>
                <div>
                  <h1 className="id">#{p.id}</h1>
                  <h1 className="names">{p.name.toUpperCase()}</h1>                  
                  <h2 className="id"> Types:</h2>
                    {p.types.length === 2 ? (
                          <div>
                            <h3 className="type1">
                            <ul className="type">
                              <li className="typeLiDetail">
                                {
                                typeof p.types[0] === 'string' ? p.types[0] : p.types[0]?.name}-  
                                {
                                typeof p.types[1] === 'string' ? p.types[1] : p.types[1]?.name}
                        
                              </li>
                            </ul>
                            </h3>
                          </div>
                          ) : (
                          <div>
                            <h3 className="typeLiDetail2">{
                            typeof p.types[0] === 'string' ? p.types[0] : p.types[0]?.name}</h3>
                          </div>
                    )}
                  <img  className="imagen" src={p.image} alt=""/>
                  <div>                    
                    <h2 className="id"> Stats:  </h2>
                    <ul >                      
                        <li className="list">Hp: {p.hp}</li>
                        <li>Attack: {p.attack}</li>
                        <li>Defense: {p.defense}</li>
                        <li>Speed: {p.speed}</li>
                        <li>Height: {p.height}</li>
                        <li>Weight: {p.weight}</li>
                    </ul>                    
                  </div>
                </div> 
              </div> 
              </>              
          ))
        ) : (
          <img
            src={"https://media.tenor.com/fSsxftCb8w0AAAAd/pikachu-running.gif"}
            width="250px" height="300px"
            alt="Not found"
          />
        )}
      </div>
      <div >
      <Link to="/home" className="backLink"> Back </Link> </div>
    </div>
    </>
  );
}
