import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {useHistory} from "react-router-dom"
import { getTypes, postPokemon } from "../redux/actions/index.js";
import "./CreatePage.css";

// function validate(pokemon){
//   let errors = {};
//   if (!pokemon.name){
//     errors.name = "Please write a name"
//   } return errors
// }

const validateForm = ({name, image, hp, attack, defense, speed, height, weight,}) => {
  const errors = {};
  const urlRegex = /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/;
  const onlyLowerCases = /^[a-z]+$/;
  const onlyLetters = /[\^$.¡*+?=¿%&!:|\\/()[\]{}1234567890¬"'#;-@¨]/;  
  const onlyNumbers = /[\^$.¡*+?=¿%&!:|\\/()[\]{}¬"'#;-@¨]/;
 
  if (
    typeof name !== "string" || name.length <= 2 || name.length >= 25 || onlyLetters.test(name) || onlyLowerCases.test(name)
  ){
    errors.name = "Please write between 2-25 lowercases";    
  }
    
  else if (!urlRegex.test(image))
    errors.image = "Please provide a valid URL for the image";
  else if (isNaN(hp) || onlyNumbers.test(hp) || hp <= 1 || hp > 500)
    errors.hp = "Hp must be between 1-500";
  else if (isNaN(attack) || onlyNumbers.test(attack) || attack <= 1|| attack > 150)
    errors.attack = "Attack must be between 1-150";
  else if (isNaN(defense) || onlyNumbers.test(defense) || defense <= 1|| defense > 150)
    errors.defense = "Defense must be between 1-150";  
  else if (isNaN(speed) || onlyNumbers.test(speed) || speed <= 1|| speed > 300)
    errors.speed = "Speed must be between 1-300";
  else if (isNaN(height) || onlyNumbers.test(height) || height <= 1 || height > 50)
    errors.height = "Height must be between 1-50";
  else if (isNaN(weight) || onlyNumbers.test(weight) || weight <= 0|| weight > 2000)
    errors.weight = "Weight must be between 0-2000";  
  return errors;
};


export default function PokemonCreate() {
  const dispatch = useDispatch();
  const history = useHistory();
  const types = useSelector((state) => state.types);

  const [errors,setErrors] = useState({});

  const [pokemon, setPokemon] = useState({
    name: "",
    types: [],
    image: "",
    hp: 0,
    attack: 0,
    defense: 0,
    speed: 0,
    height: 0,
    weight: 0,
  });

  useEffect(() => {
    dispatch(getTypes());
  }, []);

  useEffect(() => {
    setErrors(validateForm(pokemon));
  }, [pokemon]);

  function handleSelect(e) {
    setPokemon({
      ...pokemon,
      types: [...pokemon.types, e.target.value],
    });
  }


function onInputChange(e) {
  e.preventDefault();
  setPokemon({
    ...pokemon,
    [e.target.name]: e.target.value,
  });
}


function onSubmit(e) {
  e.preventDefault();
  if (Object.keys(errors).length > 0) {
    alert("Please correct the errors in the form");
    return;
  }
  dispatch(postPokemon(pokemon));
  alert("Pokemon has been created");
  setPokemon({
    name: "",
    types: [],
    image: "",
    hp: 0,
    attack: 0,
    defense: 0,
    speed: 0,
    height: 0,
    weight: 0,
  });
  history.push("/home");
}

  return (
    <form className="form" onSubmit={onSubmit}>
      <h3 className="title"> Create your Pokemon here</h3>
      
        <label for="name"> Name: </label>
        <input
          onChange={onInputChange}
          id="name"
          name="name"
          type="text"
          value={pokemon.name}
          className="input"
        />{" "}
        {
                error.name &&
                <span className="error">{errors.name}</span>
              }
     
     
        <label htmlFor="">Image: </label>
        <input
          onChange={onInputChange}
          name="image"
          type="text"
          value={pokemon.image}
          className="input"
        />{" "}
        {errors.image && <p className="error"> {errors.image}</p>}
      
        {" "}
        <label htmlFor="">Hp: </label>
        <input
          onChange={onInputChange}
          name="hp"
          type="number"
          value={pokemon.hp}
          className="input"
        />{" "}
    
     
        <label htmlFor="">Attack: </label>
        <input
          onChange={onInputChange}
          name="attack"
          type="number"
          value={pokemon.attack}
          className="input"
        />{" "}
     
     
        <label htmlFor="">Defense: </label>
        <input
          onChange={onInputChange}
          name="defense"
          type="number"
          value={pokemon.defense}
          className="input"
        />{" "}
     
     
        <label htmlFor="">Speed: </label>
        <input
          onChange={onInputChange}
          name="speed"
          type="number"
          value={pokemon.speed}
          className="input"
        />{" "}
      
     
        {" "}
        <label htmlFor="">Height: </label>
        <input
          onChange={onInputChange}
          name="height"
          type="number"
          value={pokemon.height}
          className="input"
        />{" "}
     
     
        <label htmlFor="">Weight: </label>
        <input
          onChange={onInputChange}
          name="weight"
          type="number"
          value={pokemon.weight}
          className="input"
        />{" "}
      
      
        {" "}
        <label htmlFor="">Types: </label>
        <p className="types-s">
        <select onChange={handleSelect}>
          {types.map((e) => (
            <option  value={e.name}>{e.name}</option>
          ))}{" "}
        </select>
        </p>
        <p className="types-s">
        <select onChange={handleSelect}>
          {types.map((e) => (
            <option  value={e.name}>{e.name}</option>
          ))}{" "}
        </select>
        </p>
        <Link to="/home">
      <button type="submit" className="atras">Back</button>
        </Link>
      <button type="submit" className="bottom">Create Pokemon</button>
    </form>
  );
}
